#include <iostream>
using namespace std;

int main() {
    
    // Edit Anything Except Values
    int a = 100;
    int b = -100;
    short c = 100;
    double d = 500.550;
    
    // Do Not Edit
    cout << a << "\n"; // 100
    cout << b << "\n"; // -100
    cout << c << "\n"; // 100
    cout << d << "\n"; // 500.550
    
    return 0;
}